challenges.starterCode:
fun main() {
    val score = 85
    // Use if/else if/else to determine grade

}

challenges.solution:
fun main() {
    val score = 85
    if (score >= 90) {
        println("A")
    } else if (score >= 80) {
        println("B")
    } else if (score >= 70) {
        println("C")
    } else if (score >= 60) {
        println("D")
    } else {
        println("F")
    }
}

